mapper.select:
select id, channel, version, version_no, force_update, url, create_time, remark, state, update_time
from app_version where deleted = false

    and channel = #{param.channel}


    and state = #{param.state}


    and remark like concat('%', #{param.queryName, typeHandler=LikeTypeHandler } ,'%')

order by id desc

select id, channel, version,version_no, force_update, url, create_time, remark, deleted, update_time
from app_version
where channel = #{channel}
and deleted = false
and force_update = true
and version_no > #{startVersion}
and version_no < #{endVersion}
