mapper.select:
select classify, ask, answer from help_center where deleted = false and state = true and classify = #{classify}
and (ask like concat('%', #{queryName, typeHandler=com.eghm.handler.mysql.LikeTypeHandler}, '%') or answer like concat('%', #{queryName, typeHandler=com.eghm.handler.mysql.LikeTypeHandler} ,'%'))
order by sort
