mapper.select:
select id, ask, help_type, sort, state, create_time, update_time from help_center where deleted = false
and help_type = #{param.helpType}
and state = #{param.state}
and ask like concat('%', #{param.queryName, typeHandler=LikeTypeHandler}, '%')
order by sort, id desc

select help_type, ask, answer from help_center where deleted = false and state = true and help_type = #{helpType}
and (ask like concat('%', #{queryName, typeHandler=LikeTypeHandler}, '%') or answer like concat('%', #{queryName, typeHandler=LikeTypeHandler} ,'%'))
order by sort
