mapper.sql:
select s.* from ( select t.id, t.title, t.longitude, t.latitude, st_distance_sphere(point(t.longitude, t.latitude),point(#{param.longitude}, #{param.latitude})) as distance, t.`level`, t.city_id as cityId, t.county_id as countyId, t.detail_address as detailAddress, t.cover_url as coverUrl, t.phone, t.tag as tagIds, r.salePrice from homestay t inner join (select homestay_id, count(1) as staynum, min(sale_price) as salePrice from homestay_room_config where config_date between #{param.startDate} and #{param.endDate} and stock > 0 and state = true group by homestay_id, homestay_room_id having count(homestay_room_id) = #{param.stayNum}) r on t.id = r.homestay_id where t.state = 1 and t.platform_state = 2 and t.deleted = false
and t.title like concat('%', #{param.queryName, typeHandler=com.eghm.handler.mysql.LikeTypeHandler}, '%')
and t.`level` = #{param.level}
) s
order by s.distance, s.id desc
order by r.salePrice, s.id desc
