mapper.sql:
select t.id, t.title, t.longitude, t.latitude,
st_distance_sphere(point(t.longitude, t.latitude), point(#{param.longitude}, #{param.latitude})) as distance,
t.`level`, t.city_id as cityId, t.county_id as countyId, t.detail_address as detailAddress, t.cover_url as coverUrl, t.phone, t.tag as tagIds, count(r.id) as num from homestay t left join homestay_room_config r on t.id = r.homestay_id where t.state = 1 and t.platform_state = 2 and t.deleted = false and r.config_date between #{param.startDate} and #{param.endDate}
and t.title like concat('%', #{param.queryName, typeHandler=com.eghm.handler.mysql.LikeTypeHandler}, '%')
and t.`level` = #{param.level}
GROUP BY t.id having count(r.id) = datediff(#{param.endDate}, #{param.startDate}) order by id desc

select s.* from ( select id, title, longitude, latitude, st_distance_sphere(point(longitude, latitude),point(#{param.longitude}, #{param.latitude})) as distance, `level`, city_id as cityId, county_id as countyId, detail_address as detailAddress, cover_url as coverUrl, phone, tag as tagIds from homestay where state = 1 and platform_state = 2 and deleted = false
and title like concat('%', #{param.queryName, typeHandler=com.eghm.handler.mysql.LikeTypeHandler}, '%')
and `level` = #{param.level}
) s order by s.distance, s.id desc
