mapper.select:
select t.order_no, r.cover_url, r.title, m.title as homestayName, r.pay_type, r.nick_name, r.mobile, r.num, r.state, r.pay_time, r.pay_amount, r.discount_amount, r.create_time from `order` r inner join homestay_order t on t.order_no = r.order_no left join homestay m on t.homestay_id = m.id where t.deleted = false
and r.merchant_id = #{param.merchantId}
and (r.order_no like concat('%', #{param.queryName, typeHandler=LikeTypeHandler}, '%') or r.title like concat('%', #{param.queryName, typeHandler=LikeTypeHandler}, '%') or m.title like concat('%', #{param.queryName, typeHandler=LikeTypeHandler}, '%') or r.mobile like concat('%', #{param.queryName, typeHandler=LikeTypeHandler}, '%') )
and r.state = #{param.orderState}
and r.coupon_id is not null
and r.coupon_id is null
and r.pay_time between #{param.startDate} and #{param.endDate}
order by t.id desc

select t.order_no, r.cover_url, r.title, m.title as homestayName, t.homestay_id, r.pay_type, r.num, r.state, r.pay_amount from `order` r inner join homestay_order t on t.order_no = r.order_no left join homestay m on t.homestay_id = m.id where t.deleted = false and r.member_id = #{param.memberId}
and r.title like concat('%', #{param.queryName, typeHandler=LikeTypeHandler}, '%')
and r.state = #{param.orderState}
order by t.id desc
