mapper.insert:
insert into homestay_room_config (id, homestay_room_id, homestay_id, config_date, state, line_price, sale_price, stock) values
(#{id}, #{homestayRoomId}, #{homestayId}, #{configDate}, #{state},  #{linePrice}, #{salePrice}, #{stock})
on duplicate key update state = #{state}, line_price = #{linePrice}, sale_price = #{salePrice}, stock = #{stock}

mapper.update:
update homestay_room_config set stock = stock + #{num} and sale_num = sale_num - #{num}
where
  homestay_room_id = #{roomId}
  and stock + #{num} >= 0
  and config_date >= #{startDate}
  and config_date < #{endDate}
  and state = true

mapper.select:
select min(sale_price) as minPrice, homestay_id as homestayId from homestay_room_config where config_date between #{startDate} and #{endDate} and state = true and stock > 0 and homestay_id in
#{item}
group by homestay_id

select
    min(sale_price)
from homestay_room_config where config_date between #{startDate} and #{endDate} and state = true and stock > 0 and homestay_room_id = #{roomId}
