mapper.select:
select t.id, t.title, t.cover_url, r.title as homestayTitle, t.state, t.recommend, t.dimension, t.resident, t.room_type, t.refund_type, t.create_time, t.update_time from homestay_room t left join homestay r on t.homestay_id = r.id where t.deleted = false and r.deleted = false
and t.title like concat('%', #{param.queryName, typeHandler=com.eghm.handler.mysql.LikeTypeHandler} ,'%')
and t.state = #{param.state}
and t.refund_type = #{param.refundType}
and t.merchant_id = #{param.merchantId}
order by t.id desc

select id, title, cover_url, room_type from homestay_room where state = 1 and deleted = false and homestay_id = #{param.homestayId}
and title like concat('%', #{param.queryName, typeHandler=com.eghm.handler.mysql.LikeTypeHandler}, '%')
and room_type = #{param.roomType}
and resident >= #{resident}
order by id desc

select create_date as createDate, sum(id) as appendNum from homestay_room where create_date >= #{startDate} and create_date <= #{endDate} and deleted = false
and merchant_id = #{merchantId}
group by create_date

select t.id, t.title, t.cover_url, 'homestay' as productType, t.state, r.title as storeName from homestay_room t inner join homestay r on t.homestay_id = r.id where t.deleted = false
and (t.title like concat('%', #{param.queryName, typeHandler=com.eghm.handler.mysql.LikeTypeHandler} ,'%') or r.title like concat('%', #{param.queryName, typeHandler=com.eghm.handler.mysql.LikeTypeHandler} ,'%'))
and t.state = #{param.state}
and t.merchant_id = #{param.merchantId}
order by t.id desc
