mapper.select:
select r.order_no, r.cover_url, r.title, t.title as storeName, r.pay_type, r.num, r.state, r.create_time, r.nick_name, r.mobile, r.pay_time, r.discount_amount, r.delivery_type, r.pay_amount from `order` r left join item_store t on t.id = r.store_id where t.deleted = false
and r.merchant_id = #{param.merchantId}
and r.title like concat('%', #{param.queryName, typeHandler=com.eghm.handler.mysql.LikeTypeHandler}, '%')
and r.state = #{param.orderState}
and r.refund_state = #{param.refundState}
and r.delivery_type = #{param.deliveryType}
and r.coupon_id is not null
and r.coupon_id is null
and r.pay_time between #{param.startDate} and #{param.endDate}
order by t.id desc

select r.order_no, r.cover_url, r.title, r.store_id, t.title as storeName, r.pay_type, r.num, r.state, r.pay_amount from `order` r left join item_store t on t.id = r.store_id where t.deleted = false and r.member_id = #{param.memberId}
and r.title like concat('%', #{param.queryName, typeHandler=com.eghm.handler.mysql.LikeTypeHandler}, '%')
and r.state = #{param.orderState}
order by t.id desc
