mapper.select:
select t.id, t.title, r.merchant_name as merchantName, s.title as travelAgencyName, t.state, t.platform_state as platformState, t.start_province_id as startProvinceId, t.start_city_id as startCityId, t.cover_url as coverUrl, t.sale_num as totalNum, t.duration, t.create_time as createTime, t.update_time as updateTime from line t left join merchant r on t.merchant_id = r.id left join travel_agency s on t.travel_agency_id = s.id where t.deleted = 0
and t.state = #{params.state}
and t.duration = #{params.duration}
and t.platform_state = #{params.platformState}
and t.travel_agency_id = #{params.travelAgencyId}
and t.start_province_id = #{params.startProvinceId}
and t.start_city_id = #{params.startCityId}
and (t.title like concat('%', #{param.queryName, typeHandler=com.eghm.handler.mysql.LikeTypeHandler}, '%') or r.merchant_name like concat('%', #{param.queryName, typeHandler=com.eghm.handler.mysql.LikeTypeHandler}, '%'))
order by t.id desc

select id, title, start_city_id as startCityId, cover_url as coverUrl, total_num as totalNum, duration from line where deleted = false and state = 2 and platform_state = 2
and title like concat('%', #{param.queryName, typeHandler=com.eghm.handler.mysql.LikeTypeHandler}, '%')
and start_province_id = #{param.startProvinceId}
and start_city_id = #{param.startCityId}
and duration = #{param.duration}
order by total_num desc
order by id desc
