mapper.select:
select t.id, t.title, s.title as travelAgencyName, t.state, t.hot_sell, t.start_province_id, t.start_city_id, t.cover_url, t.sale_num, t.duration, t.create_time, t.update_time from line t left join travel_agency s on t.travel_agency_id = s.id where t.deleted = 0
and t.state = #{param.state}
and t.merchant_id = #{param.merchantId}
and t.duration = #{param.duration}
and t.travel_agency_id = #{param.travelAgencyId}
and t.start_city_id = #{param.startCityId}
and t.title like concat('%', #{param.queryName, typeHandler=com.eghm.handler.mysql.LikeTypeHandler}, '%')
order by t.id desc

select t.id, t.title, t.travel_agency_id, s.logo_url as travelLogoUrl, s.title as travelAgencyName, t.start_city_id, t.cover_url, t.total_num, t.duration from line t left join travel_agency s on t.travel_agency_id = s.id where t.deleted = false and t.state = 1
and t.title like concat('%', #{param.queryName, typeHandler=com.eghm.handler.mysql.LikeTypeHandler}, '%')
and t.start_province_id = #{param.startProvinceId}
and t.start_city_id = #{param.startCityId}
and t.travel_agency_id = #{param.travelAgencyId}
and t.duration = #{param.duration}
order by t.total_num desc
order by t.id desc

select create_date as createDate, sum(id) as appendNum from line where create_date >= #{startDate} and create_date <= #{endDate} and deleted = false
and merchant_id = #{merchantId}
group by create_date

select t.id, t.title, t.cover_url, 'line' as productType, t.state, r.title as storeName from line t inner join travel_agency r on t.travel_agency_id = r.id where t.deleted = false
and (t.title like concat('%', #{param.queryName, typeHandler=com.eghm.handler.mysql.LikeTypeHandler} ,'%') or r.title like concat('%', #{param.queryName, typeHandler=com.eghm.handler.mysql.LikeTypeHandler} ,'%'))
and t.state = #{param.state}
and t.merchant_id = #{param.merchantId}
order by t.id desc
