mapper.select:
select t.order_no, r.cover_url, r.title, m.title as travelName, r.pay_type, r.pay_time, r.create_time, r.nick_name, r.mobile, r.num, r.state, r.discount_amount, r.pay_amount from `order` r inner join line_order t on t.order_no = r.order_no left join travel_agency m on t.travel_agency_id = m.id where t.deleted = false
and r.merchant_id = #{param.merchantId}
and r.title like concat('%', #{param.queryName, typeHandler=com.eghm.handler.mysql.LikeTypeHandler}, '%')
and r.state = #{param.orderState}
and r.coupon_id is not null
and r.coupon_id is null
and r.pay_time between #{param.startDate} and #{param.endDate}
order by t.id desc

select t.order_no, r.cover_url, r.title, m.title as travelName, t.travel_agency_id, r.pay_type, r.num, r.state, r.pay_amount from `order` r inner join line_order t on t.order_no = r.order_no left join travel_agency m on t.travel_agency_id = m.id where t.deleted = false and r.member_id = #{param.memberId}
and r.title like concat('%', #{param.queryName, typeHandler=com.eghm.handler.mysql.LikeTypeHandler}, '%')
and r.state = #{param.orderState}
order by t.id desc
