mapper.select:
select
    r.prize_name as prizeTitle,
    t.winning,
    r.win_num,
    t.create_time
from lottery_log t left join lottery_prize r on t.prize_id = r.id
where t.member_id = #{param.memberId}

    and t.winning = #{param.winning}


    and t.lottery_id = #{param.lotteryId}

order by t.id desc
