mapper.select:
select id, title, start_time, end_time, state, lottery_day, lottery_total, win_num from lottery where deleted = false and merchant_id = #{param.merchantId}
and title concat('%', #{param.queryName, typeHandler=com.eghm.handler.mysql.LikeTypeHandler}, '%')
and state = #{param.state}
order by state asc, id desc

select id, title, banner_url from lottery where deleted = false and merchant_id = #{merchantId} and now() < end_time
and store_id = #{storeId}
order by state desc
