mapper.select:
select id, url, user_name, request, ip, business_time, create_time from manage_log
and (url like concat('%',#{param.queryName, typeHandler=com.eghm.handler.mysql.LikeTypeHandler },'%') or user_name like concat('%',#{param.queryName, typeHandler=com.eghm.handler.mysql.LikeTypeHandler},'%') or ip like concat('%',#{param.queryName, typeHandler=com.eghm.handler.mysql.LikeTypeHandler},'%') )
and create_time >= #{param.startTime}
and create_time < #{param.endTime}
order by id desc

select
    response
from manage_log where id = #{id}
