mapper.select:
select id, pay_channel, notify_id, step_type, trade_no, refund_no, params, create_time from pay_notify_log where deleted = false
and pay_channel = #{param.payChannel}
and step_type = #{param.stepType}
and (trade_no like concat('%',#{param.queryName, typeHandler=com.eghm.handler.mysql.LikeTypeHandler},'%') or refund_no like concat('%',#{param.queryName, typeHandler=com.eghm.handler.mysql.LikeTypeHandler},'%'))
order by id desc
