mapper.select:
select id, pay_channel, order_no, step_type , trade_no, refund_no, request_body, response_body, create_time from pay_request_log where deleted = false
and pay_channel = #{param.payChannel}
and step_type = #{param.stepType}
and (order_no like concat('%',#{param.queryName, typeHandler=LikeTypeHandler},'%') or trade_no like concat('%',#{param.queryName, typeHandler=LikeTypeHandler},'%') or refund_no like concat('%',#{param.queryName, typeHandler=LikeTypeHandler},'%'))
order by id desc
