mapper.select:
select id, pay_channel, order_no, step_type , out_trade_no, out_refund_no, request_body, response_body, create_time from pay_request_log where deleted = false
and pay_channel = #{param.payChannel}
and step_type = #{param.stepType}
and (order_no like concat('%',#{param.queryName, typeHandler=com.eghm.handler.mysql.LikeTypeHandler},'%') or out_trade_no like concat('%',#{param.queryName, typeHandler=com.eghm.handler.mysql.LikeTypeHandler},'%') or out_refund_no like concat('%',#{param.queryName, typeHandler=com.eghm.handler.mysql.LikeTypeHandler},'%'))
order by id desc
