mapper.select:
select t.id, t.title, t.cover_url, t.area_code, r.title as areaTitle, t.detail_address, t.latitude, t.longitude, t.create_time, t.update_time, s.title as typeTitle from poi_point t left join poi_area r on t.area_code = r.code left join poi_type s on t.type_id = s.id where t.deleted = false and r.deleted = false
and t.area_code = #{param.areaCode}
and t.type_id = #{param.typeId}
and t.title like concat('%', #{param.queryName, typeHandler=LikeTypeHandler}, '%')
order by id desc

select id, title, cover_url, latitude, longitude, introduce, detail_address from poi_point where deleted = false and area_code = #{areaCode}
and title like concat('%', #{queryName, typeHandler=LikeTypeHandler}, '%')
order by id desc
