mapper.select:
select t.id, t.store_id as storeId, t.title, r.title as shopName, t.sale_num as saleNum, t.state, t.platform_state as platformState, t.quota, t.delivery_type as deliveryType, t.refund_type as refundType, t.max_price as maxPrice, t.min_price as minPrice, t.create_time as createTime, t.update_time as updateTime from product t left join product_shop r on t.store_id = r.id where t.deleted = false
and (t.title like concat('%', #{param.queryName} ,'%') or r.title like concat('%', #{param.queryName} ,'%'))
and t.delivery_type = #{param.deliveryType}
and t.state = #{param.state}
and t.platform_state = #{param.platformState}
and t.store_id = #{param.storeId}
order by t.id desc
