mapper.select:
select t.id, t.logo_url, r.merchant_name, t.title, t.state, t.open_time, t.detail_address, t.phone, t.create_time, t.update_time from restaurant t left join merchant r on t.merchant_id = r.id where t.deleted = false
and t.title like concat('%', #{param.queryName, typeHandler=com.eghm.handler.mysql.LikeTypeHandler}, '%')
and t.state = #{param.state}
and t.merchant_id = #{param.merchantId}
order by t.id desc

select id, title, state from restaurant where deleted = false
and merchant_id = #{merchantId}
order by id desc

mapper.sql:
select s.* from ( select id, title, longitude, latitude, open_time, st_distance_sphere(point(longitude, latitude),point(#{param.longitude}, #{param.latitude})) as distance, cover_url, logo_url, phone from restaurant where state = 1 and deleted = false
and title like concat('%', #{param.queryName, typeHandler=com.eghm.handler.mysql.LikeTypeHandler}, '%')
) s order by s.distance, s.id desc

mapper.select:
select t.id as storeId, t.title as storeName, t.state, t.cover_url as coverUrl, 'voucher' as productType, r.merchant_name from restaurant t left join merchant r on t.merchant_id = r.id where t.deleted = false
and t.state = #{param.state}
and t.merchant_id = #{param.merchantId}
and ( t.title like concat('%', #{param.queryName, typeHandler=com.eghm.handler.mysql.LikeTypeHandler}, '%') or r.merchant_name like concat('%', #{param.queryName, typeHandler=com.eghm.handler.mysql.LikeTypeHandler}, '%') )
order by t.id desc
