mapper.select:
select t.order_no as orderNo, r.cover_url as coverUrl, r.title, m.title as restaurantName, t.restaurant_id as restaurantId, r.pay_type as payType, r.num, r.state, r.pay_amount as payAmount from `order` r inner join restaurant_order t on t.order_no = r.order_no left join restaurant m on t.restaurant_id = m.id where t.deleted = false
and (r.title like concat('%', #{param.queryName, typeHandler=com.eghm.handler.mysql.LikeTypeHandler}, '%') or r.order_no like concat('%', #{param.queryName, typeHandler=com.eghm.handler.mysql.LikeTypeHandler}, '%') or r.nick_name like concat('%', #{param.queryName, typeHandler=com.eghm.handler.mysql.LikeTypeHandler}, '%') or r.mobile like concat('%', #{param.queryName, typeHandler=com.eghm.handler.mysql.LikeTypeHandler}, '%'))
and r.state = #{param.orderState}
and t.restaurant_id = #{param.restaurantId}
and r.coupon_id is not null
and r.coupon_id is null
and r.pay_time between #{param.createDate} and #{param.endDate}
order by t.id desc

select t.order_no as orderNo, r.cover_url as coverUrl, r.title, m.title as restaurantName, t.restaurant_id as restaurantId, r.pay_type as payType, r.num, r.state, r.pay_amount as payAmount from `order` r inner join restaurant_order t on t.order_no = r.order_no left join restaurant m on t.restaurant_id = m.id where t.deleted = false and r.member_id = #{param.memberId}
and r.title like concat('%', #{param.queryName, typeHandler=com.eghm.handler.mysql.LikeTypeHandler}, '%')
and r.state = #{param.orderState}
order by t.id desc
