mapper.select:
select t.id, t.title, t.scenic_id, t.state, t.category, t.line_price, t.sale_price, t.advance_day, t.quota, t.start_date, t.end_date, t.stock, t.verification_type, t.refund_type, t.real_buy, r.scenic_name from scenic_ticket t left join scenic r on t.scenic_id = r.id where t.deleted = false and r.merchant_id = #{param.merchantId}
and t.scenic_id = #{param.scenicId}
and t.category = #{param.category}
and t.state = #{param.state}
and t.title like concat('%', #{param.queryName, typeHandler=com.eghm.handler.mysql.LikeTypeHandler} ,'%')
order by t.id desc

select create_date as createDate, sum(id) as appendNum from scenic_ticket where create_date >= #{startDate} and create_date <= #{endDate}
and merchant_id = #{merchantId}
group by create_date
