mapper.select:
select t.id, t.title, t.scenic_id, t.state, t.category, t.sale_num, t.sale_price, t.start_date, t.end_date, t.stock, t.real_buy, r.scenic_name, t.create_time, t.update_time from scenic_ticket t left join scenic r on t.scenic_id = r.id where t.deleted = false and t.merchant_id = #{param.merchantId}
and t.scenic_id = #{param.scenicId}
and t.category = #{param.category}
and t.state = #{param.state}
and t.title like concat('%', #{param.queryName, typeHandler=LikeTypeHandler} ,'%')
order by t.id desc

select t.id, t.title, r.cover_url, 'ticket' as productType, t.state, t.state = 2 as disabled, r.scenic_name as storeName from scenic_ticket t inner join scenic r on t.scenic_id = r.id where t.deleted = false
and (t.title like concat('%', #{param.queryName, typeHandler=LikeTypeHandler} ,'%') or r.scenic_name like concat('%', #{param.queryName, typeHandler=LikeTypeHandler} ,'%'))
and t.state = #{param.state}
and t.merchant_id = #{param.merchantId}
order by t.id desc
