mapper.select:
select t.order_no, r.title, t.scenic_name, r.pay_type, r.num, r.mobile, r.state, r.discount_amount, r.pay_amount, r.pay_time, r.create_time from ticket_order t inner join `order` r on t.order_no = r.order_no where t.deleted = false
and r.merchant_id = #{param.merchantId}
and (t.order_no like concat('%', #{param.queryName, typeHandler=com.eghm.handler.mysql.LikeTypeHandler}, '%') or r.title like concat('%', #{param.queryName, typeHandler=com.eghm.handler.mysql.LikeTypeHandler}, '%') or t.scenic_name like concat('%', #{param.queryName, typeHandler=com.eghm.handler.mysql.LikeTypeHandler}, '%') or r.mobile like concat('%', #{param.queryName, typeHandler=com.eghm.handler.mysql.LikeTypeHandler}, '%') )
and r.state = #{param.state}
and r.coupon_id is not null
and r.coupon_id is null
and r.pay_time between #{param.startDate} and #{param.endDate}
and r.close_type = #{param.closeType}
order by t.id desc

select t.order_no, r.cover_url, r.title, t.scenic_name, r.pay_type, r.num, r.state, r.pay_amount from `order` r inner join ticket_order t on t.order_no = r.order_no where t.deleted = false and r.member_id = #{param.memberId}
and (r.title like concat('%', #{param.queryName, typeHandler=com.eghm.handler.mysql.LikeTypeHandler}, '%') or t.scenic_name like concat('%', #{param.queryName, typeHandler=com.eghm.handler.mysql.LikeTypeHandler}, '%'))
and r.state = #{param.orderState}
and r.pay_time between #{param.startDate} and #{param.endDate}
order by t.id desc
