mapper.select:
select id, title, phone, cover_url, logo_url, city_id, county_id, detail_address, score, state, create_time, update_time from travel_agency where deleted = false
and merchant_id = #{param.merchantId}
and state = #{param.state}
and title like concat('%', #{param.queryName, typeHandler=com.eghm.handler.mysql.LikeTypeHandler}, '%')
order by id desc

select id, title, state from travel_agency where deleted = false
and merchant_id = #{merchantId}
order by id desc

select t.id as storeId, t.title as storeName, t.state, t.cover_url as coverUrl, 'line' as productType, r.merchant_name from travel_agency t left join merchant r on t.merchant_id = r.id where t.deleted = false
and t.state = #{param.state}
and t.merchant_id = #{param.merchantId}
and ( t.title like concat('%', #{param.queryName, typeHandler=com.eghm.handler.mysql.LikeTypeHandler}, '%') or r.merchant_name like concat('%', #{param.queryName, typeHandler=com.eghm.handler.mysql.LikeTypeHandler}, '%') )
order by t.id desc
