mapper.select:
select t.id, t.title, t.venue_type, t.cover_url, t.open_time, t.telephone, t.detail_address from venue t where t.deleted = false
and t.merchant_id = #{param.merchantId}
and t.state = #{param.state}
and t.title like concat('%', #{param.queryName, typeHandler=com.eghm.handler.mysql.LikeTypeHandler}, '%')
and t.venue_type = #{param.venueType}

mapper.sql:
select s.* from ( select t.id, t.title, t.venue_type, t.cover_url, t.longitude, t.latitude, st_distance_sphere(point(t.longitude, t.latitude), point(#{param.longitude}, #{param.latitude})) as distance, t.city_id as cityId, t.county_id as countyId, t.detail_address from venue t where t.state = 1 and t.deleted = false
and t.title like concat('%', #{param.queryName, typeHandler=com.eghm.handler.mysql.LikeTypeHandler}, '%')
and t.venue_type = #{param.venueType}
) s
order by s.distance, s.id desc
order by s.distance desc, s.id desc
