mapper.select:
select t.order_no, t.site_title, t.title, t.venue_type, r.pay_type, r.pay_amount, r.discount_amount, r.nick_name, r.mobile, r.create_time, r.pay_time, r.state from `order` r inner join venue_order t on t.order_no = r.order_no where t.deleted = false
and r.merchant_id = #{param.merchantId}
and (t.order_no like concat('%', #{param.queryName, typeHandler=LikeTypeHandler}, '%') or t.title like concat('%', #{param.queryName, typeHandler=LikeTypeHandler}, '%') or t.site_title like concat('%', #{param.queryName, typeHandler=LikeTypeHandler}, '%') or r.mobile like concat('%', #{param.queryName, typeHandler=LikeTypeHandler}, '%'))
and r.state = #{param.state}
and r.coupon_id is not null
and r.coupon_id is null
and r.pay_time between #{param.startDate} and #{param.endDate}
and r.close_type = #{param.closeType}
order by t.id desc

select t.order_no, t.site_title, t.title, t.venue_type, t.cover_url, r.pay_type, r.pay_amount, r.refund_state, r.state from `order` r inner join venue_order t on t.order_no = r.order_no where t.deleted = false and r.member_id = #{param.memberId}
and (t.title like concat('%', #{param.queryName, typeHandler=LikeTypeHandler}, '%') or t.site_title like concat('%', #{param.queryName, typeHandler=LikeTypeHandler}, '%'))
and r.state = #{param.state}
and r.pay_time between #{param.startDate} and #{param.endDate}
order by t.id desc
