mapper.select:
select t.id, t.cover_url, t.venue_id, r.title as venueName, t.title, t.state, t.sort, t.create_time, t.update_time from venue_site t left join venue r on t.venue_id = r.id where t.deleted = false
and t.venue_id = #{param.venueId}
and t.merchant_id = #{param.merchantId}
and t.state = #{param.state}
and t.title like concat('%', #{param.queryName, typeHandler=com.eghm.handler.mysql.LikeTypeHandler}, '%')
order by t.sort

select t.id, t.title, t.cover_url, 'venue' as productType, t.state, t.state = 2 as disabled, r.title as storeName from venue_site t inner join venue r on t.venue_id = r.id where t.deleted = false
and (t.title like concat('%', #{param.queryName, typeHandler=com.eghm.handler.mysql.LikeTypeHandler} ,'%') or r.title like concat('%', #{param.queryName, typeHandler=com.eghm.handler.mysql.LikeTypeHandler} ,'%'))
and t.state = #{param.state}
and t.merchant_id = #{param.merchantId}
order by t.id desc
